import React, { useEffect, useState } from 'react'
import { handleError, handleSuccess } from '../../../../utils/utils';
import { ToastContainer } from 'react-toastify';
import { useNavigate } from 'react-router-dom';
import styles from './Profile.module.css';
import Navbar from '../../../../components/navbar/Navbar';
import Footer from '../../../../components/footer/Footer';

function Profile() {
    const [dataSupplier, setDataSupplier] = useState('');
    const navigate = useNavigate();

    const handleLogout = (e) => {
        localStorage.removeItem('token');
        localStorage.removeItem('role');
        handleSuccess('User Loggedout');
        setTimeout(() => {
            navigate('/supplier-login');
        }, 500)
    }

    useEffect(() => {
        const fetchProducts = async () => {
            try {
                const url = `http://localhost:8080/auth/supplier/data-supplier`;
                const headers = {
                    headers: {
                        'Authorization': localStorage.getItem('token'),
                    }
                }
                const response = await fetch(url, headers);
                const result = await response.json();
                console.log(result);
                // i need change because always come data index 0
                setDataSupplier(result[0]);
            } catch (err) {
                handleError(err);
            }
        }
            fetchProducts();
    }, []);

    return(
        <section className={styles.profileBody}>
            <Navbar 
                two="Orders"
                two1="Under preparing orders"
                pathTwo1="/supplier/cement/under-preparing-orders"
                two2="Pending orders"
                pathTwo2="/supplier/cement/pending-orders"
                two3="Old orders"
                pathTwo3="/supplier/cement/old-orders"
                four="Profile"
                pathFour="/supplier/cement/profile"
                logout={handleLogout}
            />

            <div className={styles.profileContainer}>
                <div className={styles.profileRow}>
                    <h1 className={styles.profileH1}>{dataSupplier?.supplierName} Profile</h1>
                    <p><strong>Supplier Name:</strong> {dataSupplier?.supplierName}</p>
                    <p><strong>supplier ID:</strong> {dataSupplier?.username}</p>
                    <p><strong>Email:</strong> {dataSupplier?.email}</p>
                    <p><strong>Phone:</strong> {dataSupplier?.supplierPhone}</p>
                    <p><strong>Product:</strong> {dataSupplier?.supplierProduct}</p>
                    <p>
                        <strong>Commercial Register: </strong> 
                        {dataSupplier?.commercialRegister && (
                            <a href={`http://localhost:5000/uploads/${dataSupplier.commercialRegister}`} target="_blank" rel="noopener noreferrer">View PDF</a>
                        )}
                    </p>
                </div>
            </div>

            <Footer 
                two="Orders"
                two1="Under preparing orders"
                pathTwo1="/supplier/cement/under-preparing-orders"
                two2="Pending orders"
                pathTwo2="/supplier/cement/pending-orders"
                two3="Old orders"
                pathTwo3="/supplier/cement/old-orders"
                four="Profile"
                pathFour="/supplier/cement/profile"
                logout={handleLogout}
            />
            <ToastContainer />
        </section>
    );
}

export { Profile };
