import React, { useState } from "react";
import { Link } from "react-router-dom";
import styles from "./Navbar.module.css";

function Navbar(props) {
    const [isActive, setIsActive] = useState(false);
    const [dropdownActive, setDropdownActive] = useState(false);

    const toggleNavbar = () => {
        setIsActive(prev => !prev); // Toggle the state
    };
    
    const toggleDropdown = () => {
        setDropdownActive(prev => !prev);
    };

    return (
        <nav className={styles.navbar}>
            <div className={styles.container}>
                <img src="/images/logo.png" alt="logo" className={styles.logo} />
                <button
                    className={styles.toggleButton}
                    aria-expanded={isActive}
                    aria-label="Toggle navigation"
                    onClick={toggleNavbar}
                >
                    ☰
                </button>
                {/* The navbarCollapse class dynamically changes based on the isActive state */}
                <div className={`${styles.navbarCollapse} ${isActive ? styles.active : ''}`}>
                    <ul className={styles.navbarNav}>
                        <li className={styles.navItem}>
                            <Link className={styles.navLink} to="/company/home">{props.one}</Link>
                        </li>
                        <li className={`${styles.navItem} ${styles.dropdown}`}>
                            <button
                                className={`${styles.navLink} ${styles.dropdownToggle}`}
                                aria-expanded={dropdownActive}
                                onClick={toggleDropdown}
                            >
                                {props.two}
                            </button>
                            <ul className={`${styles.dropdownMenu} ${dropdownActive ? styles.show : ''}`}>
                                <li className={styles.dropdownItem}>
                                    <Link className={styles.navLink} to="/company/home/preparing-orders">Preparing orders</Link>
                                </li>
                                <li className={styles.dropdownItem}>
                                    <Link className={styles.navLink} to="/company/home/pending-orders">Pending orders</Link>
                                </li>
                                <li className={styles.dropdownItem}>
                                    <Link className={styles.navLink} to="/company/home/past-orders">Past orders</Link>
                                </li>
                            </ul>
                        </li>
                        <li className={styles.navItem}>
                            <Link className={styles.navLink} to="/company/home/cement-order">{props.three}</Link>
                        </li>
                        <li className={styles.navItem}>
                            <Link className={styles.navLink} to="/company/home/concrete-order">{props.four}</Link>
                        </li>
                        <li className={styles.navItem}>
                            <Link className={styles.navLink} to="/company/home/profile">{props.five}</Link>
                        </li>
                    </ul>
                </div>
            </div>
        </nav>
    );
}

export default Navbar;
