import { useNavigate } from 'react-router-dom';
import { handleSuccess } from '../../../../utils/utils';
import { ToastContainer } from 'react-toastify';
import styles from './PendingOrders.module.css';
import Navbar from '../../../../components/navbar/Navbar';
import Footer from '../../../../components/footer/Footer';

function PendingOrders() {

    const navigate = useNavigate();

    const handleLogout = (e) => {
        localStorage.removeItem('token');
        localStorage.removeItem('role');
        handleSuccess('User Loggedout');
        setTimeout(() => {
            navigate('/company-login');
        }, 1000)
    }

    return(
        <section className={styles.PendingOrdersBody}>
            <Navbar 
                one="Home"
                pathOne="/company/home"
                two="Orders"
                two1="Under preparing orders"
                pathTwo1="/company/home/under-preparing-orders"
                two2="Pending orders"
                pathTwo2="/company/home/pending-orders"
                two3="Old orders"
                pathTwo3="/company/home/old-orders"
                three="Cement"
                pathThree="/company/home/cement-order"
                four="Concrete"
                pathFour="/company/home/concrete-order"
                five="Profile"
                pathFive="/company/home/profile"
                logout={handleLogout}
            />

            <div className={styles.PendingOrdersTitle}>
                <h2 className={styles.PendingOrdersH2}>Concrete Orders</h2>
            </div>
            <div className={styles.PendingOrdersContainer}>
                {companies.length > 0 ? (
                    companies
                        .filter(company => company.s === "pending") // Filter companies where state is "new"
                        .map((company, index) => (
                            <div className={styles.profileRow} key={index}>
                                {/* Now each company will be in a separate div */}
                                {Object.entries(company).map(([key, value]) => (
                                    // Check if the key is not "__v" or "_id" before rendering
                                    key !== "__v" && key !== "_id" && key !== "state" && key !== "role" && (
                                        <div key={key}>
                                            <strong>{key}:</strong> {value?.toString()}
                                            
                                        </div>
                                    )
                                ))}
                            </div>
                        ))
                ) : (
                    <p>No companies found.</p>
                )}
            </div>

            <Footer 
                one="Home"
                pathOne="/company/home"
                two="Orders"
                two1="Under preparing orders"
                pathTwo1="/company/home/under-preparing-orders"
                two2="Pending orders"
                pathTwo2="/company/home/pending-orders"
                two3="Old orders"
                pathTwo3="/company/home/old-orders"
                three="Cement"
                pathThree="/company/home/cement-order"
                four="Concrete"
                pathFour="/company/home/concrete-order"
                five="Profile"
                pathFive="/company/home/profile"
                logout={handleLogout}
            />
            <ToastContainer />
        </section>
    );
}
export default PendingOrders;
