import React, { useEffect, useState } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import { handleError, handleSuccess } from '../../../../../utils/utils';
import { ToastContainer } from 'react-toastify';
import styles from './CementBill.module.css';
import Navbar from '../../../../../components/navbar/Navbar';
import Footer from '../../../../../components/footer/Footer';

function CementBill() {
    const { inputValue } = useParams();
    console.log(inputValue)
    const [dataCompany, setDataCompany] = useState('');
    const [cementBillInfo, setcementBillInfo] = useState({
        companyName: dataCompany?.companyName,
        companyPhone: dataCompany?.companyPhone,
        recipientName: '',
        recipientPhone: '',
        location: '',
        deliveryTime: '',
        cementQuantity: {inputValue}
    });

    const navigate = useNavigate();

    const handleLogout = (e) => {
        localStorage.removeItem('token');
        localStorage.removeItem('role');
        handleSuccess('User Loggedout');
        setTimeout(() => {
            navigate('/company-login');
        }, 1000)
    }

    const handleChange = (e) => {
        const { name, value } = e.target;
        console.log(name, value);
        const copyCementBillInfo = { ...cementBillInfo };
        copyCementBillInfo[name] = value;
        setcementBillInfo(copyCementBillInfo);
    }

    // Phone منع ادخال احرف مثلا في ال 
    const handleKeyPress = (e) => {
        if (!/^[0-9]$/.test(e.key)) {
            e.preventDefault(); // منع الإدخال إذا لم يكن رقماً
            handleError("Please enter a number");
        }
    };
    
    // Phone number must be 10 digits
    const validatePhoneNumber = (e) => {
        if (!/^[0-9]{10}$/) {
            handleError("Phone number must be 10 digits");
        }
    };

    const handleCementBill = async (e) => {
        e.preventDefault();
        // تحديد الحقول المطلوبة
        const requiredFields = ['recipientName', 'recipientPhone', 'location', 'deliveryTime'];

        // التحقق من وجود الحقول المطلوبة
        const missingFields = requiredFields.filter(field => !cementBillInfo[field]);

        if (missingFields.length > 0) {
            return handleError(`The following fields are required: ${missingFields.join(', ')}`);
        }

        try {
            const url = '#';
            const response = await fetch(url, {
                method: "POST",
                headers: {
                    'Content-Type': 'application/json'
                },
                body: JSON.stringify(cementBillInfo)
            });
            const result = await response.json();
            const { success, message, error } = result;
            if (success) {
                handleSuccess(message);
                setTimeout(() => {
                    navigate('/company/home/under-preparing-orders')
                }, 1000)
            } else if (error) {
                const details = error?.details[0].message;
                handleError(details);
            } else if (!success) {
                handleError(message);
            }
            console.log(result);
        } catch (err) {
            handleError(err);
        }
    }

    useEffect(() => {
        const fetchProducts = async () => {
            try {
                const url = `http://localhost:8080/auth/company/data-company`;
                const headers = {
                    headers: {
                        'Authorization': localStorage.getItem('token'),
                    }
                }
                const response = await fetch(url, headers);
                const result = await response.json();
                console.log(result);
                setDataCompany(result[0]);
            } catch (err) {
                handleError(err);
            }
        }
            fetchProducts();
    }, []);

    return(
        <section className={styles.cementBillBody}>
            <Navbar 
                one="Home"
                pathOne="/company/home"
                two="Orders"
                two1="Preparing orders"
                pathTwo1="/company/home/preparing-orders"
                two2="Pending orders"
                pathTwo2="/company/home/pending-orders"
                two3="Past orders"
                pathTwo3="/company/home/past-orders"
                three="Cement"
                pathThree="/company/home/cement-order"
                four="Concrete"
                pathFour="/company/home/concrete-order"
                five="Profile"
                pathFive="/company/home/profile"
                logout={handleLogout}
            />

            <div className={styles.cementBillContainer}>
                <div className={styles.cementBillRow}>
                    <h1 className={styles.cementBillH1}>Cement Bill</h1>
                    <form className={styles.cementBillForm} onSubmit={handleCementBill} >
                        <div className={styles.cementBillDiv}>
                            <p className={styles.cementBillP}><strong>Company name:</strong> {dataCompany?.companyName}</p>
                        </div>
                        <div className={styles.cementBillDiv}>
                            <p className={styles.cementBillP}><strong>Company phone:</strong> {dataCompany?.companyPhone}</p>
                        </div>
                        <div className={styles.cementBillDiv}>
                            <label className={styles.cementBillLabel} htmlFor='recipientName'><strong>Recipient's name</strong></label>
                            <input
                                className={styles.cementBillInput}
                                onChange={handleChange}
                                type='text'
                                name='recipientName'
                                placeholder="Enter the recipient's name"
                                value={cementBillInfo.companyName}
                                autoFocus
                            />
                        </div>
                        <div className={styles.cementBillDiv}>
                            <label className={styles.cementBillLabel} htmlFor='recipientPhone'><strong>Recipient's phone</strong></label>
                            <input
                                className={styles.cementBillInput}
                                onChange={handleChange}
                                onKeyPress={handleKeyPress}
                                onSubmit={validatePhoneNumber}
                                type='tel'
                                name='recipientPhone'
                                inputmode="numeric" 
                                maxlength="10"
                                placeholder="Enter the recipient's phone"
                                value={cementBillInfo.recipientPhone}
                            />
                        </div>
                        <div className={styles.cementBillDiv}>
                            <label className={styles.cementBillLabel} htmlFor='location'><strong>Location</strong></label>
                            <input
                                className={styles.cementBillInput}
                                onChange={handleChange}
                                type='text'
                                name='location'
                                placeholder='ex:Governorate/City/Area/Neighborhood/Street Name'
                                value={cementBillInfo.location}
                            />
                        </div>
                        <div className={styles.cementBillDiv}>
                            <label className={styles.cementBillLabel} htmlFor='deliveryTime'><strong>Delivery time</strong></label>
                            <input
                                className={styles.cementBillInputTime}
                                onChange={handleChange}
                                type='datetime-local'
                                name='deliveryTime'
                                value={cementBillInfo.deliveryTime}
                            />
                        </div>
                        <div className={styles.cementBillDiv}>
                            <p className={styles.cementBillP}><strong>Quantity is:<br /></strong> {inputValue} tons</p>
                        </div>
                        <div className={styles.cementBillDiv}>
                            <p className={styles.cementBillP}><strong>The number is:<br /></strong> {inputValue * 20} bags</p>
                        </div>
                        <div className={styles.cementBillDiv}>
                            <p className={styles.cementBillP}><strong>The price:<br /></strong> {inputValue * 20 * 5} JD</p>
                        </div>
                        <button className={styles.cementBillButton} type='submit'>confirm order</button>
                    </form>
                </div>
            </div>

            <Footer 
                one="Home"
                pathOne="/company/home"
                two="Orders"
                two1="Preparing orders"
                pathTwo1="/company/home/preparing-orders"
                two2="Pending orders"
                pathTwo2="/company/home/pending-orders"
                two3="Past orders"
                pathTwo3="/company/home/past-orders"
                three="Cement"
                pathThree="/company/home/cement-order"
                four="Concrete"
                pathFour="/company/home/concrete-order"
                five="Profile"
                pathFive="/company/home/profile"
                logout={handleLogout}
            />
            <ToastContainer />
        </section>
    );
}

export default CementBill;
