import React, { useState } from 'react'
import { handleError, handleSuccess } from '../../../../utils/utils';
import { ToastContainer } from 'react-toastify';
import { useNavigate } from 'react-router-dom';
import styles from './CementOrder.module.css';
import Navbar from '../../../../components/navbar/Navbar';
import Footer from '../../../../components/footer/Footer';

function CementOrder() {
    const [inputValue, setInputValue] = useState('');
    const navigate = useNavigate();

    const handleLogout = (e) => {
        localStorage.removeItem('token');
        localStorage.removeItem('role');
        handleSuccess('User Loggedout');
        setTimeout(() => {
            navigate('/company-login');
        }, 1000)
    }

    const handleChange = (e) => {
        setInputValue(e.target.value);
    }

    const validateNumber = () => {
        const value = inputValue.trim();
        const isValidNumber = /^(\d+(\.\d*)?|\.\d+)$/.test(value); // Regex for integer or float
      
        if (!isValidNumber) {
          // Show an error message or handle invalid input
          console.log('Please enter a valid number.');
        }
      };
      

    const handleCheckout = (e) => {
        e.preventDefault();
        localStorage.setItem('inputValue', inputValue);
        setTimeout(() => { 
            navigate('/company/home/cement-order/cement-bill') // (function) سيتم تنفيذها بعد انتهاء الوقت
        }, 1000)
    }
    return(
        <section className={styles.profileBody}>
            <Navbar 
                one="Home"
                pathOne="/company/home"
                two="Orders"
                two1="Preparing orders"
                pathTwo1="/company/home/preparing-orders"
                two2="Pending orders"
                pathTwo2="/company/home/pending-orders"
                two3="Past orders"
                pathTwo3="/company/home/past-orders"
                three="Cement"
                pathThree="/company/home/cement-order"
                four="Concrete"
                pathFour="/company/home/concrete-order"
                five="Profile"
                pathFive="/company/home/profile"
                logout={handleLogout}
            />

            <div className={styles.cementOrderContainer}>
                <div className={styles.cementOrderRow}>
                    <h1 className={styles.cementOrderH1}>Cement Order</h1>
                    <form className={styles.cementOrderForm} onSubmit= {handleCheckout}>
                        <div className={styles.cementOrderDiv}>
                            <label className={styles.cementOrderLabel} htmlFor='amountOfCement'>Enter the required amount of cement in tons</label>
                            <input
                                className={styles.cementOrderInput}
                                onChange= {handleChange}
                                onBlur={validateNumber}
                                // type='number'
                                name='amountOfCement'
                                inputmode="numeric" 
                                placeholder='Enter the required amount of cement in tons...'
                                value={inputValue}
                            />
                        </div>
                        <div className={styles.cementOrderDiv}>
                            <p className={styles.cementOrderP}>20 bags of cement equals 1 tons</p>
                        </div>
                        <button className={styles.cementOrderButton} type='submit'>checkout</button>
                    </form>
                </div>
            </div>

            <Footer 
                one="Home"
                pathOne="/company/home"
                two="Orders"
                two1="Preparing orders"
                pathTwo1="/company/home/preparing-orders"
                two2="Pending orders"
                pathTwo2="/company/home/pending-orders"
                two3="Past orders"
                pathTwo3="/company/home/past-orders"
                three="Cement"
                pathThree="/company/home/cement-order"
                four="Concrete"
                pathFour="/company/home/concrete-order"
                five="Profile"
                pathFive="/company/home/profile"
                logout={handleLogout}
            />
            <ToastContainer />
        </section>
    );
}
export default CementOrder;
