import React, { useEffect, useState } from 'react';
import { useNavigate } from 'react-router-dom';
import { handleSuccess } from '../../../../../utils/utils';
import { ToastContainer } from 'react-toastify';
import styles from './CementBill.module.css';
import Navbar from '../../../../../components/navbar/Navbar';
import Footer from '../../../../../components/footer/Footer';

function CementBill() {
    const [inputValue, setInputValue] = useState('');
    const navigate = useNavigate();

    const handleLogout = (e) => {
        localStorage.removeItem('token');
        localStorage.removeItem('role');
        handleSuccess('User Loggedout');
        setTimeout(() => {
            navigate('/company-login');
        }, 1000)
    }

    useEffect(() => {
        const storedValue = localStorage.getItem('inputValue');
        setInputValue(storedValue);
        const fetchProducts = async () => {
            try {
                const url = `http://localhost:8080/auth/company/data-company`;
                const headers = {
                    headers: {
                        'Authorization': localStorage.getItem('token'),
                    }
                }
                const response = await fetch(url, headers);
                const result = await response.json();
                console.log(result);
                setDataCompany(result[0]);
            } catch (err) {
                handleError(err);
            }
        }
            fetchProducts();
    }, []);

    return(
        // <p>The required amount of cement is: {inputValue} tonnes</p>
        <section className={styles.cementBillBody}>
            <Navbar 
                one="Home"
                pathOne="/company/home"
                two="Orders"
                two1="Preparing orders"
                pathTwo1="/company/home/preparing-orders"
                two2="Pending orders"
                pathTwo2="/company/home/pending-orders"
                two3="Past orders"
                pathTwo3="/company/home/past-orders"
                three="Cement"
                pathThree="/company/home/cement-order"
                four="Concrete"
                pathFour="/company/home/concrete-order"
                five="Profile"
                pathFive="/company/home/profile"
                logout={handleLogout}
            />

            <div className={styles.cementBillContainer}>
                <div className={styles.cementBillRow}>
                    <h1 className={styles.cementBillH1}>Cement Order</h1>
                    <form className={styles.cementBillForm} onSubmit='' >
                        <div className={styles.cementBillDiv}>
                            <label className={styles.cementBillLabel} htmlFor='amountOfCement'>Enter the required amount of cement in tonne</label>
                            <input
                                className={styles.cementBillInput}
                                onChange= ''
                                onKeyPress=''
                                type='number'
                                name='amountOfCement'
                                inputmode="numeric" 
                                placeholder='Enter the required amount of cement in tonne...'
                                value={inputValue}
                            />
                        </div>
                        <div className={styles.cementBillDiv}>
                            <p className={styles.cementBillP}>20 bags of cement equals 1 tonne</p>
                        </div>
                        <button className={styles.cementBillButton} type='submit'>checkout</button>
                    </form>
                </div>
            </div>

            <Footer 
                one="Home"
                pathOne="/company/home"
                two="Orders"
                two1="Preparing orders"
                pathTwo1="/company/home/preparing-orders"
                two2="Pending orders"
                pathTwo2="/company/home/pending-orders"
                two3="Past orders"
                pathTwo3="/company/home/past-orders"
                three="Cement"
                pathThree="/company/home/cement-order"
                four="Concrete"
                pathFour="/company/home/concrete-order"
                five="Profile"
                pathFive="/company/home/profile"
                logout={handleLogout}
            />
            <ToastContainer />
        </section>
    );
}

export default CementBill;
